importPackage(Packages.com.vordel.trace);

function invoke(msg) 
{
	var httpRequestUri = String(msg.get("http.request.uri"));
	var httpRequestPath = msg.get("http.request.path");
	var httpMethodPath = msg.get("api.method.path");
	var destinationURL = msg.get("destinationURL");

	Trace.info("OData routing....");
	Trace.debug("httpRequestUri:		" + httpRequestUri);
	Trace.debug("httpMethodPath:	" + httpMethodPath);
	Trace.debug("destinationURL:   	" + destinationURL);

	// Remove the wildcard * at the end
	httpMethodPath=httpMethodPath.substring(0, httpMethodPath.length() - 1);
	// Perhaps improve this, as the wildcard may oocur more than once
	destinationURL = destinationURL.replace("*", "");

	// Extract the query parameters given and put them in a variable
	if(httpRequestUri.indexOf('?')!=-1) {
		var queryString = httpRequestUri.substring(httpRequestUri.indexOf('?')+1)
	}
	// Reduce the method path about the ({....}) part
	if( httpMethodPath.indexOf("(")!=-1) {
		httpMethodPath = httpMethodPath.substring(0, httpMethodPath.indexOf("("));
	}
	
	// Extract the path from the incoming request, including the declared BE-Method
	var queryPath = httpRequestPath.substring(httpRequestPath.indexOf(httpMethodPath));
	// Remove the * from the Query-Path, if present (for instance, when using Try-IT)
	queryPath = queryPath.replace("*", "");
     // And the Query-Path must be URI-Encoded
	queryPath = encodeURI(queryPath);
	// From the destinationURL we must remove everything after basePath
	destinationURL = destinationURL.substring(0, destinationURL.indexOf(httpMethodPath));

	Trace.debug("httpMethodPath:   	" + httpMethodPath);
	Trace.debug("queryPath:   	" + queryPath);
	Trace.debug("queryString:   	" + queryString);
	

	//var requestUri = httpRequestUri.substring(httpRequestUri.indexOf(httpMethodPath)+httpMethodPath.length());
	if(queryString!=null) {
		var destinationURL = destinationURL + queryPath + "?" + queryString;
	} else {
		var destinationURL = destinationURL + queryPath;
	}

	Trace.debug("OData routing ...");
	Trace.info("Generated destinationURL: " + destinationURL);

	msg.put("destinationURL", destinationURL);
	return true;         
}
